import './App.css';
import '@fortawesome/fontawesome-free/css/all.min.css';
import React, { useState } from 'react';

function App() {
  // Definición de estados
  const [inputText, setInputText] = useState('');
  const [outputText, setOutputText] = useState('');

  const handleFileSelect = (event) => {
    const fileInput = event.target.files[0];
    if (fileInput && fileInput.name.endsWith('.smia')) {
      const reader = new FileReader();
      reader.onload = (e) => {
        const textArea = document.getElementById('inputArea');
        textArea.value = e.target.result;
        setInputText(e.target.result); // Actualiza el estado del inputText
      };
      reader.readAsText(fileInput);
    } else {
      alert('Por favor, seleccione un archivo con la extensión .smia');
    }
  };

  const triggerFileSelect = () => {
    const fileInput = document.createElement('input');
    fileInput.type = 'file';
    fileInput.accept = '.smia';
    fileInput.onchange = handleFileSelect;
    fileInput.click();
  };

  const handleExecute = async () => {
    try {
      const response = await fetch('http://localhost:8080/analizador_entrada', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ texto: inputText }),
      });

      if (!response.ok) {
        throw new Error('Error en la solicitud');
      }

      const result = await response.text();
      setOutputText(result); // Actualiza el estado del outputText
    } catch (error) {
      console.error('Error al enviar los datos:', error);
      setOutputText('Error al procesar el texto'); // Actualiza el estado del outputText en caso de error
    }
  };

  return (
    <div className="container">
      <h1>Sistema De Archivos EXT2</h1>

      <div className="buttons">
        <button id="selectBtn" onClick={triggerFileSelect}>
          <i className="fas fa-folder-open"></i> Seleccionar
        </button>
        <button id="executeBtn" onClick={handleExecute}>
          <i className="fas fa-play"></i> Ejecutar
        </button>
      </div>

      <div className="input-section">
        <h2>Entrada:</h2>
        <textarea id="inputArea" value={inputText} onChange={(e) => setInputText(e.target.value)} />
      </div>

      <div className="output-section">
        <h2>Salida:</h2>
        <textarea id="outputArea" value={outputText} placeholder="Salida" readOnly />
      </div>
    </div>
  );
}

export default App;
